import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Header from './components/Header';
import VideoProcessor from './components/VideoProcessor';
import DetectionDashboard from './components/DetectionDashboard';
import ViolationHistory from './components/ViolationHistory';
import SystemHealth from './components/SystemHealth';
import './App.css';

function App() {
  const [systemHealth, setSystemHealth] = useState({});
  const [currentSession, setCurrentSession] = useState(null);
  const [detectionData, setDetectionData] = useState([]);
  const [violationData, setViolationData] = useState([]);

  // Check system health on startup
  useEffect(() => {
    checkSystemHealth();
    const interval = setInterval(checkSystemHealth, 30000); // Check every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const checkSystemHealth = async () => {
    try {
      const response = await fetch('/health');
      if (response.ok) {
        const health = await response.json();
        setSystemHealth(health);
      }
    } catch (error) {
      console.error('Health check failed:', error);
    }
  };

  const handleSessionUpdate = (session) => {
    setCurrentSession(session);
  };

  const handleDetectionUpdate = (detection) => {
    setDetectionData(prev => [...prev.slice(-99), detection]); // Keep last 100 detections
  };

  const handleViolationUpdate = (violation) => {
    setViolationData(prev => [...prev.slice(-49), violation]); // Keep last 50 violations
  };

  return (
    <Router>
      <div className="App">
        <Header systemHealth={systemHealth} />

        <main className="main-content">
          <Routes>
            <Route 
              path="/" 
              element={
                <VideoProcessor 
                  onSessionUpdate={handleSessionUpdate}
                  onDetectionUpdate={handleDetectionUpdate}
                  onViolationUpdate={handleViolationUpdate}
                  currentSession={currentSession}
                />
              } 
            />
            <Route 
              path="/dashboard" 
              element={
                <DetectionDashboard 
                  detectionData={detectionData}
                  violationData={violationData}
                  currentSession={currentSession}
                />
              } 
            />
            <Route 
              path="/history" 
              element={
                <ViolationHistory 
                  violationData={violationData}
                />
              } 
            />
            <Route 
              path="/health" 
              element={
                <SystemHealth 
                  systemHealth={systemHealth}
                  onRefresh={checkSystemHealth}
                />
              } 
            />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>

        <ToastContainer
          position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="dark"
        />
      </div>
    </Router>
  );
}

export default App;
