  // Check processing status
  const checkProcessingStatus = async () => {
    try {
      const response = await fetch('/video/status');
      if (response.ok) {
        const status = await response.json();
        console.log('ðŸ“Š Processing status:', status);

        if (status.is_processing) {
          setIsProcessing(true);
          setIsPaused(status.is_paused || false);
          setProcessingProgress(status.progress || 0);
          setProcessingId(status.session_id || null);

          // Reconnect WebSocket if processing is active and not connected
          if (!wsRef.current && status.websocket_url) {
            console.log('ðŸ”Œ Reconnecting to WebSocket...');
            connectWebSocket(status.session_id, status.websocket_url);
          }
        } else {
          setIsProcessing(false);
          setIsPaused(false);
          setProcessingId(null);
        }

        return status;
      }
    } catch (error) {
      console.error('âŒ Error checking processing status:', error);
    }
    return null;
  };

  // Handle window resize to redraw bounding boxes
  useEffect(() => {
    const handleResize = () => {
      console.log('ðŸ”„ Window resized, redrawing detections...');
      // Redraw on normal canvas
      setTimeout(() => drawDetections(detections, false), 100);
      // Redraw on full-screen canvas if in full-screen mode
