    if (detections.length > 0 && currentFrame) {
      console.log('ðŸ”„ Detections updated, redrawing...');
      // Draw on normal canvas
      setTimeout(() => drawDetections(detections, false), 50);
      // Draw on full-screen canvas if in full-screen mode
      if (isFullScreen) {
        setTimeout(() => drawDetections(detections, true), 50);
      }
    }
  }, [detections, currentFrame, isFullScreen]);

  // Check processing status on component mount
  useEffect(() => {
    console.log('ðŸ”„ Component mounted, checking processing status...');
    checkProcessingStatus();

    // Set up interval to periodically check status
    const statusInterval = setInterval(() => {
      if (!isProcessing) {
        checkProcessingStatus();
      }
    }, 5000); // Check every 5 seconds if not processing

    return () => {
      clearInterval(statusInterval);
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  // Check status when returning to this tab
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        console.log('ðŸ”„ Tab became visible, checking processing status...');
        checkProcessingStatus();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);

  // Handle keyboard events for full-screen mode and controls
  useEffect(() => {
    const handleKeyPress = (event) => {
      // Prevent default for space key to avoid page scrolling
      if (event.key === ' ' && isProcessing) {
        event.preventDefault();
      }

      if (event.key === 'Escape' && isFullScreen) {
        setIsFullScreen(false);
      }
      if (event.key === 'f' && isProcessing && currentFrame && !isFullScreen) {
        setIsFullScreen(true);
      }
      if (event.key === ' ' && isProcessing) {
        if (isPaused) {
          resumeProcessing();
        } else {
          pauseProcessing();
        }
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [isFullScreen, isProcessing, currentFrame, isPaused]);

  // Redraw detections when entering/exiting full-screen mode
  useEffect(() => {
    if (detections.length > 0 && currentFrame) {
