            onLoad={(e) => {
              console.log('ðŸŽ¯ ROI Manager: Video frame loaded');
              const img = e.target;
              const rect = img.getBoundingClientRect();
              console.log('ðŸ“ Image dimensions:', {
                natural: `${img.naturalWidth}x${img.naturalHeight}`,
                display: `${rect.width}x${rect.height}`
              });

              // Update canvas size to match image
              const canvas = canvasRef.current;
              if (canvas) {
                canvas.width = rect.width;
                canvas.height = rect.height;
                canvas.style.width = rect.width + 'px';
                canvas.style.height = rect.height + 'px';
                console.log('ðŸŽ¨ Canvas resized to match image:', `${rect.width}x${rect.height}`);
                console.log('ðŸ”§ FRAME SIZE FIX: Using actual image size instead of props');
