                const rect = e.currentTarget.getBoundingClientRect();
                const displayCoords = {
                  x: e.clientX - rect.left,
                  y: e.clientY - rect.top
                };
                const naturalCoords = {
                  x: (displayCoords.x / rect.width) * frameWidth,
                  y: (displayCoords.y / rect.height) * frameHeight
