                        setIsDrawing(false);
                        setPreviewPoint(null);
                      }
                      toast.info('Last point removed');
                    }
                  }}
                  style={{
                    padding: '4px 8px',
                    background: '#FF9800',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontSize: '11px'
                  }}
                >
                  â†¶ Undo Point
                </button>
                <button
                  onClick={() => {
                    setPolygonPoints([]);
                    setIsDrawing(false);
                    setPreviewPoint(null);
                    toast.info('Polygon cleared');
                  }}
                  style={{
                    padding: '4px 8px',
                    background: '#f44336',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontSize: '11px'
                  }}
                >
                  ðŸ—‘ï¸ Clear
                </button>
                {polygonPoints.length >= 3 && (
                  <button
                    onClick={completePolygon}
                    style={{
                      padding: '4px 8px',
                      background: '#4CAF50',
                      color: '#fff',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      fontSize: '11px'
                    }}
                  >
                    âœ… Complete
                  </button>
                )}
              </div>
            )}
          </div>
        )}

        {/* Controls */}
        <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap', alignItems: 'center' }}>
          <button
            className="btn btn-primary"
            onClick={() => setShowZones(!showZones)}
          >
            {showZones ? 'ðŸ‘ï¸ Hide Zones' : 'ðŸ‘ï¸â€ðŸ—¨ï¸ Show Zones'}
          </button>

          <button
            className="btn btn-warning"
            onClick={clearAllZones}
            disabled={zones.length === 0}
          >
            ðŸ—‘ï¸ Clear All
          </button>

          <button
            className="btn btn-success"
            onClick={exportZones}
            disabled={zones.length === 0}
          >
            ðŸ“¥ Export Zones
          </button>

          <button
            className="btn btn-info"
            onClick={() => {
              // Add a test zone to verify functionality
              const testZone = {
                id: `test_zone_${Date.now()}`,
                type: selectedZoneType,
                points: [
                  { x: 50, y: 50 },
                  { x: 200, y: 150 }
                ],
                isComplete: true,
                name: `Test ${zoneTypes[selectedZoneType].name}`,
                requiresScooper: zoneTypes[selectedZoneType].requiresScooper
              };
              setZones([...zones, testZone]);
              toast.success('Test zone added!');
            }}
          >
            ðŸ§ª Add Test Zone
          </button>

          <span style={{ marginLeft: '16px', fontSize: '14px', opacity: 0.8 }}>
            Zones: {zones.length} | {isEnabled ? 'Click and drag to create zones' : 'Enable drawing to create zones'}
          </span>
        </div>

        {/* Drawing Instructions */}
        {isEnabled && zones.length === 0 && (
          <div style={{
            padding: '12px',
            background: 'rgba(255,210,63,0.2)',
            borderRadius: '6px',
            marginTop: '8px',
            border: '2px dashed #FFD23F'
          }}>
            <div style={{ color: '#FFD23F', fontWeight: 'bold', marginBottom: '4px' }}>
              ðŸ“ How to Draw Zones:
            </div>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>
              1. Select zone type above (e.g., "ðŸ… Sauce Area")<br />
              2. Click and hold on the video to start drawing<br />
              3. Drag to create a rectangle<br />
              4. Release to complete the zone
            </div>
          </div>
        )}

        {isDrawing && (
          <div style={{
            padding: '8px 12px',
            background: 'rgba(0,212,255,0.3)',
            borderRadius: '6px',
            marginTop: '8px',
            textAlign: 'center',
            border: '2px solid #00D4FF'
          }}>
            <span style={{ color: '#00D4FF', fontWeight: 'bold' }}>
              ðŸŽ¨ Drawing in progress... Drag to set zone size
            </span>
          </div>
        )}
        </div>
      )}

      {/* Video with zone drawing overlay */}
      <div style={{
        position: 'relative',
        display: 'inline-block',
        width: '100%',
        maxWidth: '100%',
        background: '#000',
        borderRadius: '8px',
        overflow: 'hidden'
      }}>
        {/* Background video frame */}
        {currentFrame ? (
          <img
            id="roi-background-image"
            src={`data:image/jpeg;base64,${currentFrame}`}
            alt="Video frame for zone configuration"
            style={{
              width: '100%',
              height: 'auto',
              display: 'block',
              borderRadius: '8px'
            }}
            onLoad={(e) => {
              console.log('ðŸŽ¯ ROI Manager: Video frame loaded');
              const img = e.target;
              const rect = img.getBoundingClientRect();
              console.log('ðŸ“ Image dimensions:', {
                natural: `${img.naturalWidth}x${img.naturalHeight}`,
                display: `${rect.width}x${rect.height}`
              });

              // Update canvas size to match image
              const canvas = canvasRef.current;
              if (canvas) {
                canvas.width = rect.width;
                canvas.height = rect.height;
                canvas.style.width = rect.width + 'px';
                canvas.style.height = rect.height + 'px';
                console.log('ðŸŽ¨ Canvas resized to match image:', `${rect.width}x${rect.height}`);
                console.log('ðŸ”§ FRAME SIZE FIX: Using actual image size instead of props');
              }

              // Test polygon drawing immediately
              setTimeout(() => {
                drawZones();

                // Force test polygon drawing
                const canvas = canvasRef.current;
                if (canvas && drawingMode === 'polygon') {
                  const ctx = canvas.getContext('2d');
                  console.log('ðŸ§ª FORCE TEST: Drawing test polygon on canvas');

                  // Draw a test polygon in the center
                  const centerX = canvas.width / 2;
                  const centerY = canvas.height / 2;
                  const testPoints = [
                    { x: centerX - 50, y: centerY - 50 },
                    { x: centerX + 50, y: centerY - 50 },
                    { x: centerX + 50, y: centerY + 50 },
                    { x: centerX - 50, y: centerY + 50 }
                  ];

                  ctx.save();
                  ctx.strokeStyle = '#FF6B35';
                  ctx.fillStyle = 'rgba(255, 107, 53, 0.2)';
                  ctx.lineWidth = 3;

                  ctx.beginPath();
                  ctx.moveTo(testPoints[0].x, testPoints[0].y);
                  for (let i = 1; i < testPoints.length; i++) {
                    ctx.lineTo(testPoints[i].x, testPoints[i].y);
                  }
                  ctx.closePath();
                  ctx.fill();
                  ctx.stroke();

                  // Draw test points
                  testPoints.forEach((point, index) => {
                    ctx.beginPath();
                    ctx.arc(point.x, point.y, 6, 0, 2 * Math.PI);
                    ctx.fillStyle = '#FF6B35';
                    ctx.fill();
                    ctx.strokeStyle = '#fff';
                    ctx.lineWidth = 2;
                    ctx.stroke();

                    // Draw point number
                    ctx.fillStyle = '#fff';
                    ctx.font = '12px Arial';
                    ctx.textAlign = 'center';
                    ctx.fillText((index + 1).toString(), point.x, point.y + 4);
                  });

                  ctx.restore();
                  console.log('ðŸ§ª FORCE TEST: Test polygon drawn on canvas');
                }
              }, 100);
            }}
          />
        ) : (
          <div
            id="roi-background-placeholder"
            style={{
              width: '100%',
              height: '360px',
