            </div>
            {drawingMode === 'polygon' && isEnabled && (
              <div style={{
                fontSize: '11px',
                color: '#FF6B35',
                marginTop: '8px',
                fontStyle: 'italic'
              }}>
                ðŸ”º Click here to test polygon drawing
              </div>
            )}
          </div>
        )}

        {/* Zone drawing canvas overlay */}
        <canvas
          ref={canvasRef}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            cursor: isEnabled ? 'crosshair' : 'default',
            pointerEvents: isEnabled ? 'auto' : 'none',
