          onContextMenu={(e) => {
            e.preventDefault();
            // Right-click actions for polygon mode
            if (drawingMode === 'polygon' && polygonPoints.length >= 3) {
              completePolygon();
              toast.success('ðŸ”º Polygon completed with right-click!');
            } else if (drawingMode === 'polygon' && polygonPoints.length > 0) {
              // Remove last point
              const newPoints = polygonPoints.slice(0, -1);
              setPolygonPoints(newPoints);
              if (newPoints.length === 0) {
                setIsDrawing(false);
                setPreviewPoint(null);
              }
              toast.info('ðŸ”º Last point removed');
            }
          }}
        />

        {!currentFrame && (
          <div style={{
            width: '100%',
            height: '300px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(255,255,255,0.1)',
            color: 'rgba(255,255,255,0.6)'
          }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '48px', marginBottom: '16px' }}>ðŸŽ¥</div>
              <p>Video frame will appear here</p>
            </div>
          </div>
        )}
      </div>

      {/* Zone List - Only show in editing mode */}
      {isEnabled && zones.length > 0 && (
        <div style={{
          marginTop: '16px',
          padding: '16px',
          background: 'rgba(255,255,255,0.05)',
