import React, { useState, useRef, useEffect } from 'react';
import { toast } from 'react-toastify';

const ROIZoneManager = ({ 
  currentFrame, 
  onZonesUpdate, 
  existingZones = [], 
  isEnabled = true,
  frameWidth = 640,
  frameHeight = 480 
}) => {
  const canvasRef = useRef(null);
  const [zones, setZones] = useState(existingZones);
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentZone, setCurrentZone] = useState(null);
  const [selectedZoneType, setSelectedZoneType] = useState('sauce_area');
  const [editingZone, setEditingZone] = useState(null);
  const [showZones, setShowZones] = useState(true);

  // Zone types with professional configuration
  const zoneTypes = {
    sauce_area: {
      name: 'Sauce Area',
      color: '#FF6B35',
      fillColor: 'rgba(255, 107, 53, 0.2)',
      requiresScooper: true,
      icon: 'üçÖ',
      description: 'Sauce dispensing and handling area'
    },
    cheese_area: {
      name: 'Cheese Area', 
      color: '#FFD23F',
      fillColor: 'rgba(255, 210, 63, 0.2)',
      requiresScooper: true,
      icon: 'üßÄ',
      description: 'Cheese container and handling area'
    },
    meat_area: {
      name: 'Meat/Protein Area',
      color: '#FF4757',
      fillColor: 'rgba(255, 71, 87, 0.2)',
      requiresScooper: true,
      icon: 'ü•ì',
      description: 'Meat and protein ingredient area'
    },
    vegetable_area: {
      name: 'Vegetable Area',
      color: '#2ED573',
      fillColor: 'rgba(46, 213, 115, 0.2)',
      requiresScooper: true,
      icon: 'ü•¨',
      description: 'Vegetable and fresh ingredient area'
    },
    prep_surface: {
      name: 'Prep Surface',
      color: '#5352ED',
      fillColor: 'rgba(83, 82, 237, 0.2)',
      requiresScooper: false,
      icon: 'üçï',
      description: 'Pizza preparation surface (no scooper required)'
    },
    cleaning_area: {
      name: 'Cleaning Area',
      color: '#00D2D3',
      fillColor: 'rgba(0, 210, 211, 0.2)',
      requiresScooper: false,
      icon: 'üßΩ',
      description: 'Cleaning and sanitization area'
    }
  };

  useEffect(() => {
    if (onZonesUpdate) {
      onZonesUpdate(zones);
    }
  }, [zones, onZonesUpdate]);

  useEffect(() => {
    drawZones();
  }, [zones, currentFrame, showZones, frameWidth, frameHeight]);

  const getCanvasCoordinates = (event) => {
    const canvas = canvasRef.current;
    if (!canvas) return { x: 0, y: 0 };

    const rect = canvas.getBoundingClientRect();
    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;

    const coords = {
      x: (event.clientX - rect.left) * scaleX,
      y: (event.clientY - rect.top) * scaleY
    };

    console.log('üñ±Ô∏è Mouse coordinates:', {
      client: { x: event.clientX, y: event.clientY },
      rect: { left: rect.left, top: rect.top, width: rect.width, height: rect.height },
      canvas: { width: canvas.width, height: canvas.height },
      scale: { x: scaleX, y: scaleY },
      final: coords
    });

    return coords;
  };

  const handleMouseDown = (event) => {
    if (!isEnabled) {
      console.log('üö´ Drawing disabled');
      return;
    }

    console.log('üñ±Ô∏è Mouse down event triggered');
    event.preventDefault();

    const coords = getCanvasCoordinates(event);

    // Check if clicking on existing zone for editing
    const clickedZone = findZoneAtPoint(coords.x, coords.y);
    if (clickedZone) {
      console.log('‚úèÔ∏è Editing existing zone:', clickedZone.name);
      setEditingZone(clickedZone);
      return;
    }

    // Start drawing new zone
    console.log('üé® Starting new zone drawing at:', coords);
    setIsDrawing(true);
    setCurrentZone({
      id: `zone_${Date.now()}`,
      type: selectedZoneType,
      points: [coords],
      isComplete: false
    });
  };

  const handleMouseMove = (event) => {
    if (!isDrawing || !currentZone) return;

    event.preventDefault();
    const coords = getCanvasCoordinates(event);

    // Update current zone with new point for rectangle drawing
    if (currentZone.points.length === 1) {
      const updatedZone = {
        ...currentZone,
        points: [currentZone.points[0], coords]
      };
      setCurrentZone(updatedZone);
      console.log('üñ±Ô∏è Mouse move - updating zone:', updatedZone.points);
      drawZones([...zones, updatedZone]);
    }
  };

  const handleMouseUp = (event) => {
    if (!isDrawing || !currentZone) return;

    console.log('üñ±Ô∏è Mouse up event triggered');
    event.preventDefault();

    const coords = getCanvasCoordinates(event);

    // Complete rectangle zone
    if (currentZone.points.length >= 1) {
      const newZone = {
        ...currentZone,
        points: [currentZone.points[0], coords],
        isComplete: true,
        name: `${zoneTypes[selectedZoneType].name} ${zones.length + 1}`,
        requiresScooper: zoneTypes[selectedZoneType].requiresScooper
      };

      console.log('‚úÖ Zone created:', newZone);
      setZones([...zones, newZone]);
      toast.success(`${zoneTypes[selectedZoneType].name} zone created!`);
    }

    setIsDrawing(false);
    setCurrentZone(null);
  };

  const findZoneAtPoint = (x, y) => {
    return zones.find(zone => {
      if (zone.points.length === 2) {
        const [p1, p2] = zone.points;
        return x >= Math.min(p1.x, p2.x) && x <= Math.max(p1.x, p2.x) &&
               y >= Math.min(p1.y, p2.y) && y <= Math.max(p1.y, p2.y);
      }
      return false;
    });
  };

  const drawZones = (zonesToDraw = zones) => {
    const canvas = canvasRef.current;
    if (!canvas) {
      console.log('‚ùå No canvas reference');
      return;
    }

    const ctx = canvas.getContext('2d');

    // Get the background image to match its size
    const backgroundImg = document.getElementById('roi-background-image');
    if (backgroundImg) {
      const rect = backgroundImg.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;
      canvas.style.width = rect.width + 'px';
      canvas.style.height = rect.height + 'px';
    } else {
      // Fallback to provided dimensions
      canvas.width = frameWidth;
      canvas.height = frameHeight;
    }

    console.log('üé® Drawing zones on canvas:', {
      canvasSize: `${canvas.width}x${canvas.height}`,
      frameSize: `${frameWidth}x${frameHeight}`,
      zones: zonesToDraw.length,
      backgroundImg: !!backgroundImg
    });

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (!showZones) return;

    // Draw each zone
    zonesToDraw.forEach((zone, index) => {
      if (zone.points.length >= 2) {
        const zoneConfig = zoneTypes[zone.type];
        const [p1, p2] = zone.points;

        // Draw filled rectangle
        ctx.fillStyle = zoneConfig.fillColor;
        ctx.fillRect(
          Math.min(p1.x, p2.x),
          Math.min(p1.y, p2.y),
          Math.abs(p2.x - p1.x),
          Math.abs(p2.y - p1.y)
        );

        // Draw border
        ctx.strokeStyle = zoneConfig.color;
        ctx.lineWidth = editingZone?.id === zone.id ? 4 : 2;
        ctx.setLineDash(editingZone?.id === zone.id ? [5, 5] : []);
        ctx.strokeRect(
          Math.min(p1.x, p2.x),
          Math.min(p1.y, p2.y),
          Math.abs(p2.x - p1.x),
          Math.abs(p2.y - p1.y)
        );

        // Draw label
        const labelX = Math.min(p1.x, p2.x) + 5;
        const labelY = Math.min(p1.y, p2.y) + 20;

        ctx.fillStyle = zoneConfig.color;
        ctx.font = 'bold 14px Arial';
        ctx.fillText(`${zoneConfig.icon} ${zone.name}`, labelX, labelY);

        // Draw scooper requirement indicator
        const reqText = zone.requiresScooper ? 'ü•Ñ Required' : 'üö´ Not Required';
        ctx.font = '12px Arial';
        ctx.fillStyle = zone.requiresScooper ? '#FF4757' : '#2ED573';
        ctx.fillText(reqText, labelX, labelY + 18);
      }
    });

    // Draw current zone being drawn
    if (currentZone && currentZone.points.length === 2) {
      const zoneConfig = zoneTypes[currentZone.type];
      const [p1, p2] = currentZone.points;

      ctx.strokeStyle = zoneConfig.color;
      ctx.lineWidth = 3;
      ctx.setLineDash([10, 5]);
      ctx.strokeRect(
        Math.min(p1.x, p2.x),
        Math.min(p1.y, p2.y),
        Math.abs(p2.x - p1.x),
        Math.abs(p2.y - p1.y)
      );
    }
  };

  const deleteZone = (zoneId) => {
    setZones(zones.filter(zone => zone.id !== zoneId));
    setEditingZone(null);
    toast.info('Zone deleted');
  };

  const clearAllZones = () => {
    setZones([]);
    setEditingZone(null);
    toast.info('All zones cleared');
  };

  const exportZones = () => {
    const exportData = {
      zones: zones.map(zone => ({
        ...zone,
        // Convert to relative coordinates (0-1)
        points: zone.points.map(p => ({
          x: p.x / frameWidth,
          y: p.y / frameHeight
        }))
      })),
      frameSize: { width: frameWidth, height: frameHeight },
      exportedAt: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `roi_zones_${Date.now()}.json`;
    a.click();
    URL.revokeObjectURL(url);

    toast.success('Zones exported successfully!');
  };

  return (
    <div style={{ width: '100%' }}>
      {/* Zone Controls */}
      <div style={{ 
        marginBottom: '16px', 
        padding: '16px', 
        background: 'rgba(255,255,255,0.1)', 
        borderRadius: '8px' 
      }}>
        <h4 style={{ margin: '0 0 12px 0', color: '#fff' }}>üéØ ROI Zone Manager</h4>

        {/* Zone Type Selector */}
        <div style={{ marginBottom: '12px' }}>
          <label style={{ display: 'block', marginBottom: '6px', fontWeight: 'bold' }}>
            Zone Type:
          </label>
          <select
            value={selectedZoneType}
            onChange={(e) => setSelectedZoneType(e.target.value)}
            style={{
              padding: '8px',
              borderRadius: '6px',
              border: '1px solid rgba(255,255,255,0.3)',
              background: 'rgba(255,255,255,0.1)',
              color: 'white',
              width: '200px'
            }}
          >
            {Object.entries(zoneTypes).map(([key, config]) => (
              <option key={key} value={key} style={{ background: '#333', color: 'white' }}>
                {config.icon} {config.name}
              </option>
            ))}
          </select>
        </div>

        {/* Drawing Status */}
        <div style={{
          padding: '8px 12px',
          background: isEnabled ? 'rgba(0,255,136,0.2)' : 'rgba(255,255,255,0.1)',
          borderRadius: '6px',
          marginBottom: '12px',
          border: `2px solid ${isEnabled ? '#00FF88' : 'rgba(255,255,255,0.3)'}`
        }}>
          <span style={{ color: isEnabled ? '#00FF88' : '#fff' }}>
            {isEnabled ? 'üéØ Drawing Mode: ACTIVE - Click and drag to create zones' : 'üîí Drawing Mode: DISABLED'}
          </span>
        </div>

        {/* Controls */}
        <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap', alignItems: 'center' }}>
          <button
            className="btn btn-primary"
            onClick={() => setShowZones(!showZones)}
          >
            {showZones ? 'üëÅÔ∏è Hide Zones' : 'üëÅÔ∏è‚Äçüó®Ô∏è Show Zones'}
          </button>

          <button
            className="btn btn-warning"
            onClick={clearAllZones}
            disabled={zones.length === 0}
          >
            üóëÔ∏è Clear All
          </button>

          <button
            className="btn btn-success"
            onClick={exportZones}
            disabled={zones.length === 0}
          >
            üì• Export Zones
          </button>

          <button
            className="btn btn-info"
            onClick={() => {
              // Add a test zone to verify functionality
              const testZone = {
                id: `test_zone_${Date.now()}`,
                type: selectedZoneType,
                points: [
                  { x: 50, y: 50 },
                  { x: 200, y: 150 }
                ],
                isComplete: true,
                name: `Test ${zoneTypes[selectedZoneType].name}`,
                requiresScooper: zoneTypes[selectedZoneType].requiresScooper
              };
              setZones([...zones, testZone]);
              toast.success('Test zone added!');
            }}
          >
            üß™ Add Test Zone
          </button>

          <span style={{ marginLeft: '16px', fontSize: '14px', opacity: 0.8 }}>
            Zones: {zones.length} | {isEnabled ? 'Click and drag to create zones' : 'Enable drawing to create zones'}
          </span>
        </div>

        {/* Drawing Instructions */}
        {isEnabled && zones.length === 0 && (
          <div style={{
            padding: '12px',
            background: 'rgba(255,210,63,0.2)',
            borderRadius: '6px',
            marginTop: '8px',
            border: '2px dashed #FFD23F'
          }}>
            <div style={{ color: '#FFD23F', fontWeight: 'bold', marginBottom: '4px' }}>
              üìù How to Draw Zones:
            </div>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>
              1. Select zone type above (e.g., "üçÖ Sauce Area")<br />
              2. Click and hold on the video to start drawing<br />
              3. Drag to create a rectangle<br />
              4. Release to complete the zone
            </div>
          </div>
        )}

        {isDrawing && (
          <div style={{
            padding: '8px 12px',
            background: 'rgba(0,212,255,0.3)',
            borderRadius: '6px',
            marginTop: '8px',
            textAlign: 'center',
            border: '2px solid #00D4FF'
          }}>
            <span style={{ color: '#00D4FF', fontWeight: 'bold' }}>
              üé® Drawing in progress... Drag to set zone size
            </span>
          </div>
        )}
      </div>

      {/* Video with zone drawing overlay */}
      <div style={{
        position: 'relative',
        display: 'inline-block',
        width: '100%',
        maxWidth: '100%',
        background: '#000',
        borderRadius: '8px',
        overflow: 'hidden'
      }}>
        {/* Background video frame */}
        {currentFrame && (
          <img
            id="roi-background-image"
            src={`data:image/jpeg;base64,${currentFrame}`}
            alt="Video frame for zone configuration"
            style={{
              width: '100%',
              height: 'auto',
              display: 'block',
              borderRadius: '8px'
            }}
            onLoad={(e) => {
              console.log('üéØ ROI Manager: Video frame loaded');
              const img = e.target;
              const rect = img.getBoundingClientRect();
              console.log('üìê Image dimensions:', {
                natural: `${img.naturalWidth}x${img.naturalHeight}`,
                display: `${rect.width}x${rect.height}`
              });

              // Update canvas size to match image
              const canvas = canvasRef.current;
              if (canvas) {
                canvas.width = rect.width;
                canvas.height = rect.height;
                canvas.style.width = rect.width + 'px';
                canvas.style.height = rect.height + 'px';
                console.log('üé® Canvas resized to match image');
              }

              setTimeout(() => drawZones(), 100);
            }}
          />
        )}

        {/* Zone drawing canvas overlay */}
        <canvas
          ref={canvasRef}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            cursor: isEnabled ? 'crosshair' : 'default',
            pointerEvents: isEnabled ? 'auto' : 'none',
            zIndex: 20,
            borderRadius: '8px'
          }}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onContextMenu={(e) => e.preventDefault()}
        />

        {!currentFrame && (
          <div style={{
            width: '100%',
            height: '300px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(255,255,255,0.1)',
            color: 'rgba(255,255,255,0.6)'
          }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '48px', marginBottom: '16px' }}>üé•</div>
              <p>Video frame will appear here</p>
            </div>
          </div>
        )}
      </div>

      {/* Zone List */}
      {zones.length > 0 && (
        <div style={{ 
          marginTop: '16px', 
          padding: '16px', 
          background: 'rgba(255,255,255,0.05)', 
          borderRadius: '8px' 
        }}>
          <h4 style={{ margin: '0 0 12px 0' }}>üìã Active Zones ({zones.length})</h4>
          <div style={{ maxHeight: '200px', overflowY: 'auto' }}>
            {zones.map((zone, index) => {
              const config = zoneTypes[zone.type];
              return (
                <div
                  key={zone.id}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px 12px',
                    marginBottom: '8px',
                    background: editingZone?.id === zone.id ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)',
                    borderRadius: '6px',
                    border: `2px solid ${config.color}`
                  }}
                >
                  <div>
                    <strong>{config.icon} {zone.name}</strong>
                    <br />
                    <small style={{ opacity: 0.8 }}>
                      {config.description} | Scooper: {zone.requiresScooper ? 'ü•Ñ Required' : 'üö´ Not Required'}
                    </small>
                  </div>
                  <button
                    onClick={() => deleteZone(zone.id)}
                    style={{
                      background: '#FF4757',
                      border: 'none',
                      borderRadius: '4px',
                      padding: '4px 8px',
                      color: 'white',
                      cursor: 'pointer',
                      fontSize: '12px'
                    }}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ROIZoneManager;
